import React, { Component } from "react";

import  myuserService from "./userService";

class CreateUser extends Component {

  constructor(props) {
    super(props);
    this.state = {
        fname: "",
        lname: "",
        email:"",
        role:"grapefruit"
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.uService = myuserService;
  }

  handleChange(event) {
   this.setState({role: event.target.value});
  }

   handleSubmit(event) {
     alert('Your favorite flavor is: ' + this.state.role);
     event.preventDefault();
   }
  onFormSubmit = event => {
    event.preventDefault();
    this.uService.addUser(event.target.fname.value, event.target.lname.value,event.target.email.value,event.target.role.value);
    event.target.reset();
  };

  render() {
      return (
        <div>
          <h2>Create User Form</h2>
          <br/>
          <form onSubmit={this.onFormSubmit.bind(this)}>
            <label>
              First Name:
              <input type="text" name="fname"
              value={this.state.fname.value}/><br/>
            </label>
            <br/>
            <label>
              Last Name:
              <input type="text" name="lname"
              value={this.state.lname.value}/><br/>
            </label>
            <br/>
            <label>
              Email:
              <input type="text" name="email"
              value={this.state.email.value}/><br/>
            </label>
            <br/>
            <label>
              Role:
              <select value={this.state.role} onChange={this.handleChange}>
              <option value="grapefruit">Grapefruit</option>
              <option value="lime">Lime</option>
              <option value="coconut">Coconut</option>
              <option value="mango">Mango</option>
            </select>
            <br/>
            </label>
            <br/>
            <input type="submit" value="Create" />
          </form>
        </div>
      );
    }
  }
  export default CreateUser;
