import React, { Component } from "react";

import {
  Route,
  NavLink,
  HashRouter
} from "react-router-dom";

import  myCourseService from "./CourseService";

class Student extends Component {

  constructor(props) {
    super(props);
    this.state = {
        id: "",
        lname: "",
        email:"",
        role:"student"
    };
    this.handleFNameChange = this.handleFNameChange.bind(this);
    this.handleLNameChange = this.handleLNameChange.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handleRoleChange = this.handleRoleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.uService = myuserService;
  }

  handleFNameChange(event) {
   this.setState({fname: event.target.value});
  }
  handleLNameChange(event) {
   this.setState({lname: event.target.value});
  }
  handleEmailChange(event) {
   this.setState({email: event.target.value});
  }
  handleRoleChange(event) {
   this.setState({role: event.target.value});
  }

   handleSubmit(event) {
     event.preventDefault();
     this.uService.addUser(this.state.fname, this.state.lname,this.state.email,this.state.role);
     this.setState({fname: "",lname: "",email:"",role:"student"});
     event.target.reset();
     /*alert('Your role is: ' + this.state.role);*/
   }

  render() {
    return (
      <div>
        <h1>Welcome to Courses</h1>
        <h3> List of Courses </h3>
        <li><NavLink to="/Student/DataScience"> Data Science</NavLink> </li>
        <li><NavLink to="/Student/MachineLearning">Machine Learning</NavLink> </li>
        <li><NavLink to="/Student/BigData">Big Data</NavLink> </li>
        <h3> Add Course </h3>
      </div>
    );
  }
}
 
export default Student;
