import React, { Component } from "react";

import  myCourseService from "CourseService"

class CreateCourse extends Component {

  constructor(props) {
    super(props);
    this.cService = myCourseService;
  }
  onFormSubmit = event => {
    event.preventDefault();
    this.course.create(this.courseIdInput,this.courseNameInput);
    this.inputValue = "";
  };

  render() {
    return (
      <div>
        <h2>Create Course Form</h2>
        <br/>
        <form onSubmit={this.onFormSubmit}>
          <label>
            Course Id:
            <input type="text" name="CourseID"
            value={this.courseIdInput}
            onChange={this.handleCourseIdChange}/><br/>
          </label>
          <br/>
          <label>
            Course Name:
            <input type="text" name="CourseName"
            value={this.courseNameInput}
            onChange={this.handleCourseNameChange}/><br/>
          </label>
          <br/>
          <input type="submit" value="Create" />
        </form>
      </div>
    );
  }
}
export default CreateCourse;
